import React, { Component } from 'react'; // Added { Component }
import logo from './logo.svg';
import './App.css';
import { Button, Form } from 'semantic-ui-react'

class Scores extends Component {
  constructor(props) {
    super(props);
    this.state = {Â username: ''};
    this.state = { age: ''};
    this.state = { result: []};

    this.myChangeHandler = this.myChangeHandler.bind(this);
  }
  myChangeHandler = (event) => {
    this.setState({username: event.target.value});
  }
  myChangeHandlerAge = (event) => {
    this.setState({age: event.target.value});
  }
  save = (event) => {
    var tmp = this.state.username+' ' + this.state.age;
    this.setState({ 
      result: this.state.result.concat(tmp),
  });
  event.preventDefault();
  document.getElementById("test").reset();
}

  render() {
    return (
      <div className="Scores">
        <h1>Scorelist</h1>
          <Form id="test">
            <Form.Field>
            <label>Enter your name:</label>
            <input placeholder='First Lastname' />
            </Form.Field>
            {/* <p>Enter your name:</p> */}
            <input type='text' onChange={this.myChangeHandler} />  
            <p>Enter your age:</p>
            <input type='text' onChange={this.myChangeHandlerAge} />   
          </Form>

          <button className="ui button" onClick={this.save}>

            Submit
          </button>

          <h2>List of teams:</h2>


          <ul>
            {(this.state.result).map(listitem => (
              <li key = {listitem}>{listitem}</li>  
            ))}
          </ul>



      </div>
      );
  }
}



export default Scores;
