import React, { Component } from 'react'; // Added { Component }
import logo from './logo.svg';
import './App.css';
import { Button, Form, Container } from 'semantic-ui-react'
import { Menu, Segment } from 'semantic-ui-react'

class Scores extends Component {
  constructor(props) {
    super(props);
    this.state = { username: ''};
    this.state = { age: ''};
    this.state = { result: []};

    this.myChangeHandler = this.myChangeHandler.bind(this);
  }
  myChangeHandler = (event) => {
    this.setState({username: event.target.value});
  }
  myChangeHandlerAge = (event) => {
    this.setState({age: event.target.value});
  }
  save = (event) => {
    var tmp = this.state.username+' ' + this.state.age;
    this.setState({ 
      result: this.state.result.concat(tmp),
  });
  event.preventDefault();
  document.getElementById("test").reset();
}

  render() {
    const { activeItem } = this.state

    return (    


    <Container>
          <div class="ui menu top16">
            <a class="item active">High scores</a>
            <a class="item">Info</a>
            {/* <a class="item">Upcoming Events</a> */}
          </div>

      <div className="Scores">
        <h1 class="heading top32">High score lists</h1>
          <Form id="test">
            <Form.Field>
            <label>Enter your name:</label>
            <input placeholder='First Lastname' type='text' onChange={this.myChangeHandler} />

            <label class="top32">Enter your age:</label>
            <input placeholder='Age' type='text' onChange={this.myChangeHandlerAge} />   
            </Form.Field>
          </Form>

          <button className="top32 ui primary button" onClick={this.save}>
            Submit
          </button>

          <h2 class="subheading">List of teams:</h2>
            {/* View input data in List format */}
            <ul>
              {(this.state.result).map(listitem => (
                <li key = {listitem}>{listitem}</li>  
              ))}
            </ul>
      </div>
    </Container>





      );
  }
}

export default Scores;


export class MenuExampleBasic extends Component {
  state = {}

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <Menu>
        <Menu.Item
          name='editorials'
          active={activeItem === 'editorials'}
          onClick={this.handleItemClick}
        >
          Editorials
        </Menu.Item>

        <Menu.Item
          name='reviews'
          active={activeItem === 'reviews'}
          onClick={this.handleItemClick}
        >
          Reviews
        </Menu.Item>

        <Menu.Item
          name='upcomingEvents'
          active={activeItem === 'upcomingEvents'}
          onClick={this.handleItemClick}
        >
          Upcoming Events
        </Menu.Item>
      </Menu>
    )
  }
}
